namespace AdventOfCode2022.CSharp.Six;

public class DaySix : IAdventProblemSet
{
    public string InputString = "";

    /// <inheritdoc />
    public string Description()
    {
        return "Tuning Trouble";
    }

    /// <inheritdoc />
    public int SortOrder()
    {
        return 6;
    }

    /// <inheritdoc />
    public string PartA()
    {
        return FindRepeatMarker(InputString, 4).ToString();
    }

    /// <inheritdoc />
    public string PartB()
    {
        return FindRepeatMarker(InputString, 14).ToString();
    }

    public int FindRepeatMarker(string input, int numberOfChars)
    {
        var charArray = input.ToCharArray().ToList();
        for (var i = 0; i < charArray.Count; i++)
        {
            var sub = charArray.GetRange(i, numberOfChars);
            var distinct = sub.Distinct().ToList();
            if (sub.Count == distinct.Count)
                return i + numberOfChars;
        }

        throw new Exception("Should never get here");
    }
}